mapper.select:
select 
td.order_Id,
td.state,
td.cname,
td.oct,
td.business_Category, 
td.business_Charge
from t_order td

oct>=#{time.startTime,jdbcType=TIMESTAMP}
and oct<=#{time.endTime,jdbcType=TIMESTAMP}
 and order_id=#{time.id,jdbcType=BIGINT}
 and state=#{time.state,jdbcType=VARCHAR}

select 

from t_order td
where order_id = #{orderId,jdbcType=BIGINT} and td.consumer_account_id=#{accountId,jdbcType=BIGINT}

select 

from t_order td
where order_id = #{orderId,jdbcType=BIGINT} and td.merchant_account_id=#{accountId,jdbcType=BIGINT}

mapper.update:
update t_order
set  state = #{destState,jdbcType=CHAR},sst=#{date,jdbcType=TIMESTAMP}
where order_id = #{orderId,jdbcType=BIGINT} and state=#{sourceState,jdbcType=CHAR}

update t_order
set  state = #{destState,jdbcType=CHAR},ofst=#{date,jdbcType=TIMESTAMP}
where order_id = #{orderId,jdbcType=BIGINT} and state=#{sourceState,jdbcType=CHAR}

update t_order
set  state = #{destState,jdbcType=CHAR}
where order_id = #{orderId,jdbcType=BIGINT} and state=#{sourceState,jdbcType=CHAR}

update t_order
set  state = #{destState,jdbcType=CHAR},master_name=#{merchant.mastername,jdbcType=VARCHAR},
merchant_account_id=#{merchant.accountId,jdbcType=BIGINT},
mlng=#{merchant.lng,jdbcType=DECIMAL},mlat=#{merchant.lat,jdbcType=DECIMAL},
mci=#{merchant.contactInformation,jdbcType=VARCHAR},ort=#{ort,jdbcType=TIMESTAMP} 
where order_id = #{orderId,jdbcType=BIGINT} and state=#{sourceState,jdbcType=CHAR}

update t_order
set  state = #{destState,jdbcType=CHAR} ,
  cost=#{cost,jdbcType=INTEGER}
where order_id = #{orderId,jdbcType=BIGINT} and state=#{sourceState,jdbcType=CHAR}

update t_order
set  state = #{destState,jdbcType=CHAR},oet=#{date,jdbcType=TIMESTAMP},
evaluation=#{eval,jdbcType=INTEGER},
text_evaluation=#{textEval,jdbcType=VARCHAR}
where order_id = #{orderId,jdbcType=BIGINT} and state=#{sourceState,jdbcType=CHAR}

update t_order
set  state = #{destState,jdbcType=CHAR},oet=#{date,jdbcType=TIMESTAMP}

,cause=#{fail,jdbcType=VARCHAR}

where order_id = #{orderId,jdbcType=BIGINT} and state=#{sourceState,jdbcType=CHAR}

update t_order
set  deal_id = #{dealId,jdbcType=BIGINT}
where order_id = #{orderId,jdbcType=BIGINT}

update t_order
set  state = #{destState,jdbcType=CHAR} ,
  cost=#{cost,jdbcType=INTEGER},
 cancel_flag=#{cannelFlag,jdbcType=CHAR} 
where order_id = #{orderId,jdbcType=BIGINT} and state=#{sourceState,jdbcType=CHAR}

update t_order
set  state = #{destState,jdbcType=CHAR},oet=#{date,jdbcType=TIMESTAMP},
cancel_flag=#{cannelFlag,jdbcType=CHAR} 
where order_id = #{orderId,jdbcType=BIGINT} and state=#{sourceState,jdbcType=CHAR}
