mapper.select:
select 

from t_order_notify
where order_id = #{orderId,jdbcType=BIGINT}
  and merchant_account_id = #{merchantAccountId,jdbcType=BIGINT}

mapper.delete:
delete from t_order_notify
where order_id = #{orderId,jdbcType=BIGINT}
  and merchant_account_id = #{merchantAccountId,jdbcType=BIGINT}
