mapper.update:
update t_advert
set  state = #{state,jdbcType=CHAR}
where ad_id = #{id,jdbcType=BIGINT}

update t_advert
 set  exposure =exposure+ #{added,jdbcType=CHAR}
 where ad_id = #{adId,jdbcType=BIGINT}

update t_advert
 set  click_rate =click_rate+ #{added,jdbcType=CHAR}
 where ad_id = #{adId,jdbcType=BIGINT}

mapper.select:
select  from t_advert 

  and create_time>=#{time.startTime,jdbcType=TIMESTAMP}
  and create_time<=#{time.endTime,jdbcType=TIMESTAMP}
  and state = #{time.state,jdbcType=CHAR}
  and cname = #{time.key,jdbcType=VARCHAR}
   and display_position = #{time.displayPosition,jdbcType=VARCHAR}

  order by create_time desc
