mapper.select:
select 

from merchant_area
where account_Id = #{accountId,jdbcType=BIGINT}
  and area_code = #{areaCode,jdbcType=VARCHAR}

mapper.delete:
delete from merchant_area
where account_Id = #{accountId,jdbcType=BIGINT}
  and area_code = #{areaCode,jdbcType=VARCHAR}

mapper.update:
update merchant_area
set Lng = #{lng,jdbcType=DECIMAL},
  lat = #{lat,jdbcType=DECIMAL},
  shape = #{shape,jdbcType=VARCHAR},
  point = #{point,jdbcType=VARCHAR},
  rlat = #{rlat,jdbcType=DECIMAL},
  rlng = #{rlng,jdbcType=DECIMAL}
where account_Id = #{accountId,jdbcType=BIGINT}
  and area_code = #{areaCode,jdbcType=VARCHAR}

mapper.select:
select me.account_Id,me.lat,me.lng from merchant_service ms,merchant me where ms.sls_id=#{slsId,jdbcType=INTEGER} and ms.account_Id=me.account_Id
and me.lat>= #{leftDown.lat,jdbcType=DECIMAL} and me.lat<=#{rightTop.lat,jdbcType=DECIMAL} 
and me.Lng>=#{leftDown.lng,jdbcType=DECIMAL} and me.Lng<=#{rightTop.lng,jdbcType=DECIMAL}   and me.state='0' and me.cr='0'

select

from merchant_service ms,merchant_area ma where
ms.account_Id=ma.account_Id and ms.sls_id=#{slsId,jdbcType=INTEGER}
and ma.lat<= #{point.y,jdbcType=DECIMAL} and ma.rlat>= #{point.y,jdbcType=DECIMAL} 
and ma.lng<= #{point.x,jdbcType=DECIMAL}
and ma.rlng>=#{point.x,jdbcType=DECIMAL}
