mapper.select:
select 

from t_vip
where account_Id = #{accountId,jdbcType=BIGINT}
  and sls_id = #{slsId,jdbcType=INTEGER}

mapper.delete:
delete from t_vip
where account_Id = #{accountId,jdbcType=BIGINT}
  and sls_id = #{slsId,jdbcType=INTEGER}

mapper.select:
select  from t_vip 

  and create_time>=#{time.startTime,jdbcType=TIMESTAMP}
  and create_time<=#{time.endTime,jdbcType=TIMESTAMP}
  and bpn = #{time.key,jdbcType=VARCHAR}

  order by create_time desc
