mapper.select:
select  account_Id, user_name, cname, bpn, state, role, create_time, 
  address_code,remark from users where role='C' 
  and create_time>=#{time.startTime,jdbcType=TIMESTAMP}
  and create_time<=#{time.endTime,jdbcType=TIMESTAMP}
  and state = #{time.state,jdbcType=CHAR}
  and user_name = #{time.key,jdbcType=VARCHAR}
         or account_Id = #{time.accountId,jdbcType=BIGINT}
  
  order by create_time desc

select  account_Id, user_name, cname, bpn, state, role, create_time, 
  address_code from users where role='P' 
  and create_time>=#{time.startTime,jdbcType=TIMESTAMP}
  and create_time<=#{time.endTime,jdbcType=TIMESTAMP}
  and state = #{time.state,jdbcType=CHAR}
  and user_name = #{time.key,jdbcType=VARCHAR}
   or account_Id = #{time.accountId,jdbcType=BIGINT}
  
  order by create_time desc
